fn main() {
    let input_str = "
\
                     
\
                     
\
                     
\
                     ";

    let tasten_feld = [['0', '0', '1', '0', '0'],
                       ['0', '2', '3', '4', '0'],
                       ['5', '6', '7', '8', '9'],
                       ['0', 'A', 'B', 'C', '0'],
                       ['0', '0', 'D', '0', '0']];

    let mut code = Vec::<char>::new();
    let mut zeile = 2; //Start bei 5 (Zeile 3, Spalte 0)
    let mut spalte = 0;

    for line in input_str.lines() {
        for c in line.split("") {
            match c {
                "U" => {
                    if zeile > 0 && tasten_feld[zeile - 1][spalte] != '0' {
                        zeile -= 1;
                    }
                }
                "D" => {
                    if zeile < 4 && tasten_feld[zeile + 1][spalte] != '0' {
                        zeile += 1;
                    }
                }
                "L" => {
                    if spalte > 0 && tasten_feld[zeile][spalte - 1] != '0' {
                        spalte -= 1;
                    }
                }
                "R" => {
                    if spalte < 4 && tasten_feld[zeile][spalte + 1] != '0' {
                        spalte += 1;
                    }
                }
                _ => (),
            }
        }
        code.push(tasten_feld[zeile][spalte]);
    }
    println!("{:?}", code);
}